import React, { Component } from 'react'

class Form extends Component {
	initialState = {
		complete: '',
		items: '',
	}

	state = this.initialState

	handleChange = event => {
		const { item, complete } = event.target

		this.setState({
			[item]: complete,
		})
	}

	submitForm = () => {
		this.props.handleSubmit(this.state)
		this.setState(this.initialState)
	}

	render() {
		const {item, complete } = this.state;

		return (
			<form>
				<label htmlFor="item">Item</label>
				<input
					type="text"
					name="item"
					id="item"
					value={item}
					onChange={this.handleChange} />
				<label htmlFor="complete">Complete</label>
				<input
					type="text"
					name="complete"
					id="complete"
					value={complete}
					onChange={this.handleChange} />
				<input
					type="button"
					value="Submit" onClick={this.submitForm} />
			</form>
		);
	}
}
	export default Form;
